/* @autogenerated */
package org.acme.common.infrastructure.exceptionmapper;

import java.util.HashMap;
import java.util.Map;

import org.acme.common.exception.NotFoundException;

import jakarta.ws.rs.core.Response;
import jakarta.ws.rs.ext.ExceptionMapper;
import jakarta.ws.rs.ext.Provider;
import lombok.extern.slf4j.Slf4j;

@Slf4j
@Provider
public class NotFoundExceptionMapper implements ExceptionMapper<NotFoundException> {

  @Override
  public Response toResponse(NotFoundException exception) {
    if (log.isDebugEnabled()) {
      log.info("not found exception", exception);
    } else if (log.isInfoEnabled()) {
      log.info("not found exception");
    }
    Map<String, String> error = new HashMap<>();
    error.put("reason", exception.getMessage());
    return Response.status(404).entity(error).build();
  }
}
