/* @autogenerated */
package org.acme.common.infrastructure.exceptionmapper.sql;

import java.util.HashMap;
import java.util.Map;

import org.acme.common.sql.OptimistLockException;

import jakarta.ws.rs.core.Response;
import jakarta.ws.rs.ext.ExceptionMapper;
import jakarta.ws.rs.ext.Provider;
import lombok.extern.slf4j.Slf4j;

@Slf4j
@Provider
public class OptimistLockExceptionMapper implements ExceptionMapper<OptimistLockException> {

  @Override
  public Response toResponse(OptimistLockException exception) {
    if (log.isDebugEnabled()) {
      log.info("optimist lock exception", exception);
    } else if (log.isInfoEnabled()) {
      log.info("optimist lock exception");
    }
    Map<String, String> error = new HashMap<>();
    error.put("reason", exception.getMessage());
    return Response.status(412).entity(error).build();
  }

}
