/* @autogenerated */
package org.acme.common.rest.exceptionmapper;

import java.util.Locale;

import org.acme.common.exception.ConstraintException;

import jakarta.ws.rs.core.HttpHeaders;
import jakarta.ws.rs.core.Response;
import jakarta.ws.rs.ext.ExceptionMapper;
import jakarta.ws.rs.ext.Provider;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;

@Slf4j
@Provider
@RequiredArgsConstructor
public class ConstraintExceptionMapper implements ExceptionMapper<ConstraintException> {
  private final HttpHeaders headers;

  @Override
  public Response toResponse(ConstraintException exception) {
    if (log.isDebugEnabled()) {
      log.info("constraint exception", exception);
    } else if (log.isInfoEnabled()) {
      log.info("constraint fail");
    }
    Locale locale = headers.getAcceptableLanguages().get(0);
    return Response.status(422)
        .entity(exception.getFails().map(fail -> fail.localize(locale, true)).toList()).build();
  }
}
