package org.acme.common.rest.exceptionmapper;

import java.util.HashMap;
import java.util.Map;
import org.acme.common.exception.PaymentRequiredException;
import jakarta.ws.rs.core.Response;
import jakarta.ws.rs.ext.ExceptionMapper;
import jakarta.ws.rs.ext.Provider;
import lombok.extern.slf4j.Slf4j;

@Slf4j
@Provider
public class PaymentRequiredExceptionMapper implements ExceptionMapper<PaymentRequiredException> {

  @Override
  public Response toResponse(PaymentRequiredException exception) {
    if (log.isDebugEnabled()) {
      log.info("payment required", exception);
    } else if (log.isInfoEnabled()) {
      log.info("payment required");
    }
    Map<String, String> error = new HashMap<>();
    error.put("reason", exception.getMessage());
    return Response.status(402).entity(error).build();
  }
}
