package org.acme.common.security;

import java.util.function.Function;
import org.acme.common.action.Interaction;
import org.acme.common.reactive.Stream;
import lombok.Data;
import lombok.NonNull;
import lombok.experimental.SuperBuilder;

@Data
@SuperBuilder(toBuilder = true)
public abstract class AllowPipelineStageEvent {

  /**
   * @autogenerated EntityGenerator
   */
  @NonNull
  private Stream<Allow> detail;

  @NonNull
  public final Interaction query;

  public abstract String resourceName();

  public abstract String actionName();

  public void map(Function<Allow, Allow> allowed) {
    detail = detail.map(allowed::apply);
  }

  public void flatMap(Function<Allow, Stream<Allow>> allowed) {
    detail = detail.flatMap(allowed::apply);
  }
}
