package org.acme.features.market.area.domain.model.rule;

import java.util.Iterator;

import org.acme.features.market.area.domain.model.Area;

public interface AreaUpdateRulePipeline {

  /**
   * @autogenerated AggregateGenerator
   * @param rules
   * @param area
   * @param original
   * @return
   */
  public static Area fire(Iterator<AreaUpdateRulePipeline> rules, Area area, Area original) {
    Area result = area;
    while (rules.hasNext()) {
      result = rules.next().updating(result, original);
    }
    return result;
  }

  /**
   * @autogenerated AggregateGenerator
   * @param area
   * @param original
   * @return
   */
  Area updating(Area area, Area original);
}
