package org.acme.features.market.area.infrastructure.repository;

import java.util.List;
import java.util.Optional;
import java.util.concurrent.CompletionStage;
import java.util.concurrent.ExecutionException;
import java.util.function.BiFunction;

import org.acme.common.action.Slide;
import org.acme.features.market.area.domain.gateway.AreaCursor;
import org.acme.features.market.area.domain.gateway.AreaFilter;
import org.acme.features.market.area.domain.model.Area;

class AreaSlice extends Slide<Area> {

  /**
   * @autogenerated SlideGenerator
   */
  private final CompletionStage<List<Area>> areas;

  /**
   * @autogenerated SlideGenerator
   */
  private final AreaCursor cursor;

  /**
   * @autogenerated SlideGenerator
   */
  private final AreaFilter filter;

  /**
   * @autogenerated SlideGenerator
   */
  private final BiFunction<AreaFilter, AreaCursor, Slide<Area>> gateway;

  /**
   * @autogenerated SlideGenerator
   * @param limit
   * @param areas
   * @param gateway
   * @param filter
   * @param cursor
   */
  AreaSlice(final Optional<Integer> limit, final CompletionStage<List<Area>> areas,
      final BiFunction<AreaFilter, AreaCursor, Slide<Area>> gateway, final AreaFilter filter,
      final AreaCursor cursor) {
    super(limit);
    this.areas = areas;
    this.gateway = gateway;
    this.filter = filter;
    this.cursor = cursor;
  }

  /**
   * area
   *
   * @autogenerated SlideGenerator
   * @return area
   */
  @Override
  public CompletionStage<List<Area>> get() {
    return areas;
  }

  /**
   * @autogenerated SlideGenerator
   * @param list
   * @param limit
   * @return
   */
  public Slide<Area> map(List<Area> list, int limit) {
    if (list.isEmpty()) {
      return this;
    } else {
      Area last = list.get(list.size() - 1);
      AreaCursor cr = this.cursor.withSinceUid(last.getUid().getValue()).withLimit(limit);
      return gateway.apply(this.filter, cr);
    }
  }

  /**
   * @autogenerated SlideGenerator
   * @param limit
   * @return
   */
  @Override
  public Slide<Area> next(int limit) {
    try {
      return areas.thenApply(list -> map(list, limit)).toCompletableFuture().get();
    } catch (InterruptedException | ExecutionException e) {
      throw new IllegalStateException("Unable to complete querying ", e);
    }
  }
}
