package org.acme.features.market.color.application.service.event;

import java.util.function.Consumer;
import java.util.function.UnaryOperator;

import org.acme.common.action.Interaction;
import org.acme.features.market.color.domain.gateway.ColorFilter;

import lombok.Builder;
import lombok.Data;
import lombok.EqualsAndHashCode;
import lombok.NonNull;
import lombok.With;

@Data
@Builder(toBuilder = true)
@With
@EqualsAndHashCode(onlyExplicitlyIncluded = true)
public class ColorVisibilityQueryPipelineEvent {

  /**
   * @autogenerated VisibleContentPipelineEventGenerator
   */
  @NonNull
  private final Interaction interaction;

  /**
   * @autogenerated VisibleContentPipelineEventGenerator
   */
  @NonNull
  private ColorFilter filter;

  /**
   * @autogenerated VisibleContentPipelineEventGenerator
   * @param mapper
   */
  public void map(UnaryOperator<ColorFilter> mapper) {
    filter = mapper.apply(filter);
  }

  /**
   * @autogenerated VisibleContentPipelineEventGenerator
   * @param mapper
   */
  public void peek(Consumer<ColorFilter> mapper) {
    mapper.accept(filter);
  }
}
