  /**
   * @autogenerated VisibleContentProposalGenerator
   * @param emitter
   * @param proposal
   * @return
   */
  @SuppressWarnings("unchecked")
  public static ColorRef resolveWith(final Event<? extends ColorRemoveProposal> emitter,
      final ColorRemoveProposal proposal) {
    ((Event<ColorRemoveProposal>) emitter).fire(proposal);
    return proposal.getReference();
  }

  /**
   * @autogenerated VisibleContentProposalGenerator
   */
  @NonNull
  private final Interaction interaction;

  /**
   * @autogenerated VisibleContentProposalGenerator
   */
