package org.acme.features.market.color.domain.gateway;

import java.util.List;
import java.util.Optional;

import org.acme.features.market.color.domain.model.Color;

public interface ColorCacheGateway {

  /**
   * @autogenerated CacheGatewayGenerator
   */
  public void evict();

  /**
   * @autogenerated CacheGatewayGenerator
   * @param color
   */
  public void remove(Color color);

  /**
   * @autogenerated CacheGatewayGenerator
   * @param filter
   * @param cursor
   * @return
   */
  public Optional<ColorCached> retrieve(ColorFilter filter, ColorCursor cursor);

  /**
   * @autogenerated CacheGatewayGenerator
   * @param filter
   * @param cursor
   * @param colors
   * @return
   */
  public ColorCached store(ColorFilter filter, ColorCursor cursor, List<Color> colors);

  /**
   * @autogenerated CacheGatewayGenerator
   * @param color
   */
  public void update(Color color);
}
