package org.acme.features.market.color.domain.model.rule;

import java.util.Iterator;

import org.acme.features.market.color.domain.model.Color;

public interface ColorUpdateRulePipeline {

  /**
   * @autogenerated AggregateGenerator
   * @param rules
   * @param color
   * @param original
   * @return
   */
  public static Color fire(Iterator<ColorUpdateRulePipeline> rules, Color color, Color original) {
    Color result = color;
    while (rules.hasNext()) {
      result = rules.next().updating(result, original);
    }
    return result;
  }

  /**
   * @autogenerated AggregateGenerator
   * @param color
   * @param original
   * @return
   */
  Color updating(Color color, Color original);
}
