package org.acme.features.market.fruit.application.allow;

import java.util.concurrent.CompletableFuture;

import org.acme.common.security.Allow;

import lombok.Builder;
import lombok.Data;
import lombok.EqualsAndHashCode;
import lombok.NonNull;
import lombok.With;

@Data
@With
@Builder(toBuilder = true)
@EqualsAndHashCode(onlyExplicitlyIncluded = true)
public class FruitListAllow {

  /**
   * @autogenerated EntityGenerator
   * @param allowed
   * @return
   */
  public static FruitListAllow build(final boolean allowed) {
    return build(allowed, null);
  }

  /**
   * @autogenerated EntityGenerator
   * @param allowed
   * @param reason
   * @return
   */
  public static FruitListAllow build(final boolean allowed, final String reason) {
    return FruitListAllow.builder().detail(CompletableFuture
        .completedFuture(Allow.builder().allowed(allowed).description(reason).build())).build();
  }

  /**
   * @autogenerated EntityGenerator
   */
  @NonNull
  private CompletableFuture<Allow> detail;
}
