package org.acme.features.market.fruit.application.usecase.delete;

import org.acme.common.action.Interaction;
import org.acme.features.market.fruit.application.service.proposal.FruitRemoveProposal;
import org.acme.features.market.fruit.domain.model.FruitRef;

import jakarta.enterprise.event.Event;
import lombok.Data;
import lombok.EqualsAndHashCode;
import lombok.experimental.SuperBuilder;

@Data
@SuperBuilder(toBuilder = true)
@EqualsAndHashCode(callSuper = true, onlyExplicitlyIncluded = true)
public class FruitDeleteProposal extends FruitRemoveProposal {

  /**
   * @autogenerated DeleteProposalGenerator
   * @param emitter
   * @param interaction
   * @param reference
   * @return
   */
  public static FruitRef resolveWith(final Event<FruitDeleteProposal> emitter,
      final Interaction interaction, final FruitRef reference) {
    return resolveWith(emitter,
        FruitDeleteProposal.builder().interaction(interaction).reference(reference).build());
  }
}
