package org.acme.features.market.fruit.domain.model.rule;

import java.util.Iterator;

import org.acme.features.market.fruit.domain.model.Fruit;

public interface FruitUpdateRulePipeline {

  /**
   * @autogenerated AggregateGenerator
   * @param rules
   * @param fruit
   * @param original
   * @return
   */
  public static Fruit fire(Iterator<FruitUpdateRulePipeline> rules, Fruit fruit, Fruit original) {
    Fruit result = fruit;
    while (rules.hasNext()) {
      result = rules.next().updating(result, original);
    }
    return result;
  }

  /**
   * @autogenerated AggregateGenerator
   * @param fruit
   * @param original
   * @return
   */
  Fruit updating(Fruit fruit, Fruit original);
}
