package org.acme.features.market.medal.application.usecase.delete;

import org.acme.common.action.Interaction;
import org.acme.features.market.medal.application.service.proposal.MedalRemoveProposal;
import org.acme.features.market.medal.domain.model.MedalRef;

import jakarta.enterprise.event.Event;
import lombok.Data;
import lombok.EqualsAndHashCode;
import lombok.experimental.SuperBuilder;

@Data
@SuperBuilder(toBuilder = true)
@EqualsAndHashCode(callSuper = true, onlyExplicitlyIncluded = true)
public class MedalDeleteProposal extends MedalRemoveProposal {

  /**
   * @autogenerated DeleteProposalGenerator
   * @param emitter
   * @param interaction
   * @param reference
   * @return
   */
  public static MedalRef resolveWith(final Event<MedalDeleteProposal> emitter,
      final Interaction interaction, final MedalRef reference) {
    return resolveWith(emitter,
        MedalDeleteProposal.builder().interaction(interaction).reference(reference).build());
  }
}
