package org.acme.features.market.medal.domain.model.rule;

import java.util.Iterator;

import org.acme.features.market.medal.domain.model.Medal;

public interface MedalDeleteRulePipeline {

  /**
   * @autogenerated AggregateGenerator
   * @param rules
   * @param medal
   * @return
   */
  public static Medal fire(Iterator<MedalDeleteRulePipeline> rules, Medal medal) {
    Medal result = medal;
    while (rules.hasNext()) {
      result = rules.next().deleting(result);
    }
    return result;
  }

  /**
   * @autogenerated AggregateGenerator
   * @param medal
   * @return
   */
  Medal deleting(Medal medal);
}
