package org.acme.features.market.medal.domain.model.rule;

import java.util.Iterator;

import org.acme.features.market.medal.domain.model.Medal;

public interface MedalUpdateRulePipeline {

  /**
   * @autogenerated AggregateGenerator
   * @param rules
   * @param medal
   * @param original
   * @return
   */
  public static Medal fire(Iterator<MedalUpdateRulePipeline> rules, Medal medal, Medal original) {
    Medal result = medal;
    while (rules.hasNext()) {
      result = rules.next().updating(result, original);
    }
    return result;
  }

  /**
   * @autogenerated AggregateGenerator
   * @param medal
   * @param original
   * @return
   */
  Medal updating(Medal medal, Medal original);
}
