package org.acme.features.market.merchant.domain.gateway;

import java.util.List;
import java.util.Optional;
import java.util.concurrent.CompletionStage;

import org.acme.features.market.merchant.domain.model.Merchant;

public interface MerchantCacheGateway {

  /**
   * @autogenerated CacheGatewayGenerator
   * @param merchant
   * @return
   */
  public CompletionStage<Void> remove(Merchant merchant);

  /**
   * @autogenerated CacheGatewayGenerator
   * @param filter
   * @param cursor
   * @return
   */
  public CompletionStage<Optional<MerchantCached>> retrieve(MerchantFilter filter,
      MerchantCursor cursor);

  /**
   * @autogenerated CacheGatewayGenerator
   * @param filter
   * @param cursor
   * @param merchants
   * @return
   */
  public CompletionStage<Void> store(MerchantFilter filter, MerchantCursor cursor,
      List<Merchant> merchants);

  /**
   * @autogenerated CacheGatewayGenerator
   * @param merchant
   * @return
   */
  public CompletionStage<Void> update(Merchant merchant);
}
