package org.acme.features.market.merchant.infrastructure.repository;

import java.util.List;
import java.util.Optional;
import java.util.concurrent.CompletionStage;
import java.util.concurrent.ExecutionException;
import java.util.function.BiFunction;

import org.acme.common.action.Slide;
import org.acme.features.market.merchant.domain.gateway.MerchantCursor;
import org.acme.features.market.merchant.domain.gateway.MerchantFilter;
import org.acme.features.market.merchant.domain.model.Merchant;

class MerchantSlice extends Slide<Merchant> {

  /**
   * @autogenerated SlideGenerator
   */
  private final MerchantCursor cursor;

  /**
   * @autogenerated SlideGenerator
   */
  private final MerchantFilter filter;

  /**
   * @autogenerated SlideGenerator
   */
  private final BiFunction<MerchantFilter, MerchantCursor, Slide<Merchant>> gateway;

  /**
   * @autogenerated SlideGenerator
   */
  private final CompletionStage<List<Merchant>> merchants;

  /**
   * @autogenerated SlideGenerator
   * @param limit
   * @param merchants
   * @param gateway
   * @param filter
   * @param cursor
   */
  MerchantSlice(final Optional<Integer> limit, final CompletionStage<List<Merchant>> merchants,
      final BiFunction<MerchantFilter, MerchantCursor, Slide<Merchant>> gateway,
      final MerchantFilter filter, final MerchantCursor cursor) {
    super(limit);
    this.merchants = merchants;
    this.gateway = gateway;
    this.filter = filter;
    this.cursor = cursor;
  }

  /**
   * merchant
   *
   * @autogenerated SlideGenerator
   * @return merchant
   */
  @Override
  public CompletionStage<List<Merchant>> get() {
    return merchants;
  }

  /**
   * @autogenerated SlideGenerator
   * @param list
   * @param limit
   * @return
   */
  public Slide<Merchant> map(List<Merchant> list, int limit) {
    if (list.isEmpty()) {
      return this;
    } else {
      Merchant last = list.get(list.size() - 1);
      MerchantCursor cr = this.cursor.withSinceUid(last.getUid().getValue()).withLimit(limit);
      return gateway.apply(this.filter, cr);
    }
  }

  /**
   * @autogenerated SlideGenerator
   * @param limit
   * @return
   */
  @Override
  public Slide<Merchant> next(int limit) {
    try {
      return merchants.thenApply(list -> map(list, limit)).toCompletableFuture().get();
    } catch (InterruptedException | ExecutionException e) {
      throw new IllegalStateException("Unable to complete querying ", e);
    }
  }
}
