package org.acme.features.market.place.domain.model.rule;

import java.util.Iterator;

import org.acme.features.market.place.domain.model.Place;

public interface PlaceUpdateRulePipeline {

  /**
   * @autogenerated AggregateGenerator
   * @param rules
   * @param place
   * @param original
   * @return
   */
  public static Place fire(Iterator<PlaceUpdateRulePipeline> rules, Place place, Place original) {
    Place result = place;
    while (rules.hasNext()) {
      result = rules.next().updating(result, original);
    }
    return result;
  }

  /**
   * @autogenerated AggregateGenerator
   * @param place
   * @param original
   * @return
   */
  Place updating(Place place, Place original);
}
