package org.acme.features.market.area.domain.model.valueobject;

import org.acme.common.exception.ConstraintException;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class AreaVersionVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property version of area ")
  void test_area_version_v_o_builder() {
    Assertions.assertFalse(AreaVersionVO.empty().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(AreaVersionVO.tryFrom(null).getValue().isPresent());
    Assertions.assertEquals(1, AreaVersionVO.from(1).getValue().orElse(null));
    Assertions.assertThrows(ConstraintException.class,
        () -> AreaVersionVO.tryFrom(new Object() {}));
  }
}
