package org.acme.features.market.color.domain.model.valueobject;

import org.acme.common.exception.ConstraintException;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class ColorVersionVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property version of color ")
  void test_color_version_v_o_builder() {
    Assertions.assertFalse(ColorVersionVO.empty().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(ColorVersionVO.tryFrom(null).getValue().isPresent());
    Assertions.assertEquals(1, ColorVersionVO.from(1).getValue().orElse(null));
    Assertions.assertThrows(ConstraintException.class,
        () -> ColorVersionVO.tryFrom(new Object() {}));
  }
}
