package org.acme.features.market.medal.domain.model.valueobject;

import org.acme.common.exception.ConstraintException;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class MedalVersionVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property version of medal ")
  void test_medal_version_v_o_builder() {
    Assertions.assertFalse(MedalVersionVO.empty().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(MedalVersionVO.tryFrom(null).getValue().isPresent());
    Assertions.assertEquals(1, MedalVersionVO.from(1).getValue().orElse(null));
    Assertions.assertThrows(ConstraintException.class,
        () -> MedalVersionVO.tryFrom(new Object() {}));
  }
}
