package org.acme.features.market.merchant.domain.model.valueobject;

import org.acme.common.exception.ConstraintException;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class MerchantKeyVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property key of merchant ")
  void test_merchant_key_v_o_builder() {
    Assertions.assertFalse(MerchantKeyVO.empty().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(MerchantKeyVO.tryFrom(null).getValue().isPresent());
    Assertions.assertEquals("one", MerchantKeyVO.from("one").getValue().orElse(null));
    Assertions.assertThrows(ConstraintException.class,
        () -> MerchantKeyVO.tryFrom(new Object() {}));
  }
}
