package org.acme.features.market.verify.domain.model.valueobject;

import org.acme.common.exception.ConstraintException;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class VerifyMedalVersionVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property version of verify medal ")
  void test_verify_medal_version_v_o_builder() {
    Assertions.assertFalse(VerifyMedalVersionVO.empty().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(VerifyMedalVersionVO.tryFrom(null).getValue().isPresent());
    Assertions.assertEquals(1, VerifyMedalVersionVO.from(1).getValue().orElse(null));
    Assertions.assertThrows(ConstraintException.class,
        () -> VerifyMedalVersionVO.tryFrom(new Object() {}));
  }
}
