package org.acme.features.market.verify.domain.model.valueobject;

import org.acme.common.exception.ConstraintException;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;

class VerifyVersionVOUnitTest {

  /**
   * @autogenerated ValueObjectGenerator
   */
  @Test
  @DisplayName("Test value object contruction for property version of verify ")
  void test_verify_version_v_o_builder() {
    Assertions.assertFalse(VerifyVersionVO.empty().getValue().isPresent(),
        "A empty vo should have a null value");
    Assertions.assertFalse(VerifyVersionVO.tryFrom(null).getValue().isPresent());
    Assertions.assertEquals(1, VerifyVersionVO.from(1).getValue().orElse(null));
    Assertions.assertThrows(ConstraintException.class,
        () -> VerifyVersionVO.tryFrom(new Object() {}));
  }
}
